mapper.select:
SELECT member_id,member_name, member_pwd, member_email, member_phone, member_email_addr, member_zonecode, member_addr, member_addr_detail, member_category, member_name, member_point, member_status, member_reg_date
FROM woo_member
WHERE member_id= #{member_id}

SELECT member_id,member_name, member_pwd, member_email, member_phone, member_email_addr, member_zonecode, member_addr, member_addr_detail, member_category, member_name, member_point, member_status, member_reg_date
FROM woo_member
WHERE member_id= #{member_id}

SELECT ts_idx,member_idx,subject_category_code
from teacher_subject
where member_idx=#{member_idx}

SELECT member_idx, member_id, member_name ,member_pwd, member_email, member_phone, member_email_addr, member_zonecode, member_addr, member_addr_detail, member_category, member_name, member_point, member_status, member_oauth,member_reg_date FROM woo_member
and member_category = #{member_type}
and member_reg_date >= #{search_date1}
and member_reg_date <= #{search_date2}
AND member_name LIKE CONCAT('%',#{search_word}, '%') or member_Id LIKE CONCAT('%',#{search_word}, '%')
and member_id like concat('%', #{search_word},'%')
and member_name like concat('%', #{search_word},'%' )
and member_category = 'admin'
and member_category = 'student'
and member_category = 'teacher'
ORDER by member_idx DESC LIMIT #{page_skip_count}, #{page_size}

SELECT count(*) FROM woo_member
and member_category = #{member_type}
AND member_name LIKE CONCAT('%',#{search_word}, '%') or member_Id LIKE CONCAT('%',#{search_word}, '%')
and member_reg_date >= #{search_date1}
and member_reg_date <= #{search_date2}
and member_id like concat('%', #{search_word},'%')
and member_name like concat('%', #{search_word},'%' )
and member_category = 'admin'
and member_category = 'student'
and member_category = 'teacher'

mapper.update:
update woo_member set member_status = 'Y'

    member_idx IN

        #{item}

update woo_member set member_category = #{member_category}

    member_idx IN

        #{item}

update woo_member set member_category = #{member_category},
                      member_status = #{member_status}
where member_id = #{member_id}
