mapper.update:
UPDATE woo_qna
SET ref_idx = #{qna_idx}
WHERE qna_idx = #{qna_idx}

mapper.select:
SELECT * FROM woo_qna
member_id= #{member_id}
and bbs_title like concat('%', #{search_word},'%')
and member_id like concat('%', #{search_word},'%' )
and bbs_content like concat('%', #{search_word},'%' )
limit #{page_skip_count}, #{page_size}
